    // Fungsi untuk menentukan warna kamar berdasarkan status sewa
    getKamarColor(kamar) {
      if (kamar.status === 'kosong') {
        return '#d6d6d6'; // Kamar kosong dengan warna abu-abu lembut
      }
      const today = new Date();
      const tanggalSewa = new Date(kamar.tanggalSewa);
