      const diffDays = diffTime / (1000 * 3600 * 24); // menghitung selisih dalam hari

      if (kamar.status === 'sewa') {
        if (diffDays < 0) {
          return '#f44336'; // Sudah lewat masa sewa, merah lembut
        } else if (diffDays <= 14) {
          return '#ffeb3b'; // 2 minggu lagi masa sewa habis, kuning lembut
